import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { FiPlus, FiEdit2, FiTrash2, FiSave, FiX, FiTag, FiMonitor, FiCalendar, FiMapPin, FiDollarSign, FiUsers } from 'react-icons/fi';
import './AdminPanel.css';

const AdminPanel = () => {
    const [events, setEvents] = useState([]);
    const [editingId, setEditingId] = useState(null);
    const [formData, setFormData] = useState({
        title: '',
        organization: '',
        description: '',
        venue: '',
        registration_link: '',
        start_date: '',
        end_date: '',
        location: '',
        type: '',
        price: '',
        tech_stack: [],
        speakers: [],
        virtual: false,
        tags: []
    });
    const [newTag, setNewTag] = useState('');
    const [newTech, setNewTech] = useState('');
    const [newSpeaker, setNewSpeaker] = useState('');
    const [loading, setLoading] = useState(true);

    const eventTypes = [
        'Conference',
        'Hackathon',
        'Workshop',
        'Meetup',
        'Webinar',
        'Tech Talk'
    ];

    useEffect(() => {
        fetchEvents();
    }, []);

    const fetchEvents = async () => {
        try {
            const response = await fetch('http://localhost:8000/events');
            const data = await response.json();
            setEvents(data);
        } catch (error) {
            console.error('Error fetching events:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const url = editingId
                ? `http://localhost:8000/events/${editingId}`
                : 'http://localhost:8000/events';
            const method = editingId ? 'PUT' : 'POST';

            const response = await fetch(url, {
                method,
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                },
                body: JSON.stringify(formData)
            });

            if (response.ok) {
                fetchEvents();
                resetForm();
                setEditingId(null);
            }
        } catch (error) {
            console.error('Error saving event:', error);
        }
    };

    const handleEdit = (event) => {
        setFormData({
            ...event,
            start_date: event.start_date.split('T')[0],
            end_date: event.end_date.split('T')[0]
        });
        setEditingId(event.id);
    };

    const handleDelete = async (id) => {
        if (window.confirm('Are you sure you want to delete this event?')) {
            try {
                const response = await fetch(`http://localhost:8000/events/${id}`, {
                    method: 'DELETE',
                    headers: {
                        'Authorization': `Bearer ${localStorage.getItem('token')}`
                    }
                });

                if (response.ok) {
                    fetchEvents();
                }
            } catch (error) {
                console.error('Error deleting event:', error);
            }
        }
    };

    const handleAddTag = (e) => {
        e.preventDefault();
        if (newTag.trim() && !formData.tags.includes(newTag.trim())) {
            setFormData({
                ...formData,
                tags: [...formData.tags, newTag.trim()]
            });
            setNewTag('');
        }
    };

    const handleAddTech = (e) => {
        e.preventDefault();
        if (newTech.trim() && !formData.tech_stack.includes(newTech.trim())) {
            setFormData({
                ...formData,
                tech_stack: [...formData.tech_stack, newTech.trim()]
            });
            setNewTech('');
        }
    };

    const handleAddSpeaker = (e) => {
        e.preventDefault();
        if (newSpeaker.trim() && !formData.speakers.includes(newSpeaker.trim())) {
            setFormData({
                ...formData,
                speakers: [...formData.speakers, newSpeaker.trim()]
            });
            setNewSpeaker('');
        }
    };

    const handleRemoveTag = (tagToRemove) => {
        setFormData({
            ...formData,
            tags: formData.tags.filter(tag => tag !== tagToRemove)
        });
    };

    const handleRemoveTech = (techToRemove) => {
        setFormData({
            ...formData,
            tech_stack: formData.tech_stack.filter(tech => tech !== techToRemove)
        });
    };

    const handleRemoveSpeaker = (speakerToRemove) => {
        setFormData({
            ...formData,
            speakers: formData.speakers.filter(speaker => speaker !== speakerToRemove)
        });
    };

    const resetForm = () => {
        setFormData({
            title: '',
            organization: '',
            description: '',
            venue: '',
            registration_link: '',
            start_date: '',
            end_date: '',
            location: '',
            type: '',
            price: '',
            tech_stack: [],
            speakers: [],
            virtual: false,
            tags: []
        });
    };

    return (
        <div className="admin-container">
            <motion.div 
                className="admin-header"
                initial={{ opacity: 0, y: -20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5 }}
            >
                <h1>Admin Panel</h1>
                <p>Manage tech events</p>
            </motion.div>

            <div className="admin-content">
                <motion.div 
                    className="admin-form-container"
                    initial={{ opacity: 0, x: -20 }}
                    animate={{ opacity: 1, x: 0 }}
                    transition={{ delay: 0.2 }}
                >
                    <h2>{editingId ? 'Edit Event' : 'Add New Event'}</h2>
                    <form onSubmit={handleSubmit} className="admin-form">
                        <div className="form-group">
                            <label>Title</label>
                            <input
                                type="text"
                                value={formData.title}
                                onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                                required
                            />
                        </div>

                        <div className="form-group">
                            <label>Organization</label>
                            <input
                                type="text"
                                value={formData.organization}
                                onChange={(e) => setFormData({ ...formData, organization: e.target.value })}
                                required
                            />
                        </div>

                        <div className="form-group">
                            <label>Description</label>
                            <textarea
                                value={formData.description}
                                onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                                required
                            />
                        </div>

                        <div className="form-group">
                            <label>Venue</label>
                            <input
                                type="text"
                                value={formData.venue}
                                onChange={(e) => setFormData({ ...formData, venue: e.target.value })}
                                required
                            />
                        </div>

                        <div className="form-group">
                            <label>Registration Link</label>
                            <input
                                type="url"
                                value={formData.registration_link}
                                onChange={(e) => setFormData({ ...formData, registration_link: e.target.value })}
                                required
                            />
                        </div>

                        <div className="form-row">
                            <div className="form-group">
                                <label>Start Date</label>
                                <input
                                    type="date"
                                    value={formData.start_date}
                                    onChange={(e) => setFormData({ ...formData, start_date: e.target.value })}
                                    required
                                />
                            </div>

                            <div className="form-group">
                                <label>End Date</label>
                                <input
                                    type="date"
                                    value={formData.end_date}
                                    onChange={(e) => setFormData({ ...formData, end_date: e.target.value })}
                                    required
                                />
                            </div>
                        </div>

                        <div className="form-group">
                            <label>Location</label>
                            <input
                                type="text"
                                value={formData.location}
                                onChange={(e) => setFormData({ ...formData, location: e.target.value })}
                                required
                            />
                        </div>

                        <div className="form-group">
                            <label>Event Type</label>
                            <select
                                value={formData.type}
                                onChange={(e) => setFormData({ ...formData, type: e.target.value })}
                                required
                            >
                                <option value="">Select Type</option>
                                {eventTypes.map(type => (
                                    <option key={type} value={type}>{type}</option>
                                ))}
                            </select>
                        </div>

                        <div className="form-group">
                            <label>Price</label>
                            <input
                                type="text"
                                value={formData.price}
                                onChange={(e) => setFormData({ ...formData, price: e.target.value })}
                                placeholder="Free, $99, etc."
                            />
                        </div>

                        <div className="form-group">
                            <label>Virtual Event</label>
                            <input
                                type="checkbox"
                                checked={formData.virtual}
                                onChange={(e) => setFormData({ ...formData, virtual: e.target.checked })}
                            />
                        </div>

                        <div className="form-group">
                            <label>Tech Stack</label>
                            <div className="tag-input">
                                <input
                                    type="text"
                                    value={newTech}
                                    onChange={(e) => setNewTech(e.target.value)}
                                    placeholder="Add technologies..."
                                />
                                <button type="button" onClick={handleAddTech}>
                                    <FiPlus />
                                </button>
                            </div>
                            <div className="tags-container">
                                {formData.tech_stack.map((tech, index) => (
                                    <span key={index} className="tag">
                                        <FiMonitor />
                                        {tech}
                                        <button type="button" onClick={() => handleRemoveTech(tech)}>
                                            <FiX />
                                        </button>
                                    </span>
                                ))}
                            </div>
                        </div>

                        <div className="form-group">
                            <label>Speakers</label>
                            <div className="tag-input">
                                <input
                                    type="text"
                                    value={newSpeaker}
                                    onChange={(e) => setNewSpeaker(e.target.value)}
                                    placeholder="Add speakers..."
                                />
                                <button type="button" onClick={handleAddSpeaker}>
                                    <FiPlus />
                                </button>
                            </div>
                            <div className="tags-container">
                                {formData.speakers.map((speaker, index) => (
                                    <span key={index} className="tag">
                                        <FiUsers />
                                        {speaker}
                                        <button type="button" onClick={() => handleRemoveSpeaker(speaker)}>
                                            <FiX />
                                        </button>
                                    </span>
                                ))}
                            </div>
                        </div>

                        <div className="form-group">
                            <label>Tags</label>
                            <div className="tag-input">
                                <input
                                    type="text"
                                    value={newTag}
                                    onChange={(e) => setNewTag(e.target.value)}
                                    placeholder="Add tags..."
                                />
                                <button type="button" onClick={handleAddTag}>
                                    <FiPlus />
                                </button>
                            </div>
                            <div className="tags-container">
                                {formData.tags.map((tag, index) => (
                                    <span key={index} className="tag">
                                        <FiTag />
                                        {tag}
                                        <button type="button" onClick={() => handleRemoveTag(tag)}>
                                            <FiX />
                                        </button>
                                    </span>
                                ))}
                            </div>
                        </div>

                        <div className="form-actions">
                            <motion.button
                                type="submit"
                                className="save-button"
                                whileHover={{ scale: 1.05 }}
                                whileTap={{ scale: 0.95 }}
                            >
                                <FiSave />
                                {editingId ? 'Update Event' : 'Create Event'}
                            </motion.button>
                            {editingId && (
                                <motion.button
                                    type="button"
                                    className="cancel-button"
                                    onClick={() => {
                                        resetForm();
                                        setEditingId(null);
                                    }}
                                    whileHover={{ scale: 1.05 }}
                                    whileTap={{ scale: 0.95 }}
                                >
                                    <FiX />
                                    Cancel
                                </motion.button>
                            )}
                        </div>
                    </form>
                </motion.div>

                <motion.div 
                    className="events-list"
                    initial={{ opacity: 0, x: 20 }}
                    animate={{ opacity: 1, x: 0 }}
                    transition={{ delay: 0.4 }}
                >
                    <h2>Current Events</h2>
                    {loading ? (
                        <div className="loading-spinner">
                            <div className="spinner"></div>
                        </div>
                    ) : (
                        <AnimatePresence>
                            {events.map((event) => (
                                <motion.div
                                    key={event.id}
                                    className="event-item"
                                    initial={{ opacity: 0, y: 20 }}
                                    animate={{ opacity: 1, y: 0 }}
                                    exit={{ opacity: 0, y: -20 }}
                                >
                                    <div className="event-info">
                                        <h3>{event.title}</h3>
                                        <p className="organization">{event.organization}</p>
                                        <div className="event-meta">
                                            <span>
                                                <FiCalendar />
                                                {new Date(event.start_date).toLocaleDateString()}
                                            </span>
                                            <span>
                                                <FiMapPin />
                                                {event.location}
                                            </span>
                                            {event.price && (
                                                <span>
                                                    <FiDollarSign />
                                                    {event.price}
                                                </span>
                                            )}
                                        </div>
                                    </div>
                                    <div className="event-actions">
                                        <motion.button
                                            onClick={() => handleEdit(event)}
                                            whileHover={{ scale: 1.1 }}
                                            whileTap={{ scale: 0.9 }}
                                            className="edit-button"
                                        >
                                            <FiEdit2 />
                                        </motion.button>
                                        <motion.button
                                            onClick={() => handleDelete(event.id)}
                                            whileHover={{ scale: 1.1 }}
                                            whileTap={{ scale: 0.9 }}
                                            className="delete-button"
                                        >
                                            <FiTrash2 />
                                        </motion.button>
                                    </div>
                                </motion.div>
                            ))}
                        </AnimatePresence>
                    )}
                </motion.div>
            </div>
        </div>
    );
};

export default AdminPanel;
